select round(x.avg, 4), round(y.avg, 4) from (select avg(mean_temp) as avg from weather, station, trip where date(start_time) = date and strftime('%s', end_time) - strftime('%s', start_time) <= 60 and start_station_id = station_id and station.zip_code = weather.zip_code) as x, (select avg(mean_temp) as avg from weather, station, trip where date(start_time) = date and strftime('%s', end_time) - strftime('%s', start_time) > 60 and start_station_id = station_id and station.zip_code = weather.zip_code) as y;
